import "http/requests"
import ejson "experimental/json"
import "internal/debug"
import "contrib/qxip/hash"
import "strings"
import "array"
import "join"
import "experimental/record"

aasRegistryUrl = "http://aas-registry-v3:8080"
smRegistryUrl = "http://sm-registry-v3:8080"
endpointMapping = {
    "http://aas-environment.twinficient.localhost" : "http://aas-environment:8081"
}

option requests.defaultConfig = {
    timeout: 0s,
    insecureSkipVerify: false,
}

base64UrlEncode = (url) => {
  e = hash.b64(v: url)
  return strings.trimRight(v: e, cutset: "=")
}

createHeaterShellDescriptorUrl = (id) => {
    return aasRegistryUrl + "/shell-descriptors/" + base64UrlEncode(url: id)
}

createHeaterShellDescriptorId = (company, celNr, hzNr, hgNr) => {
    return "http://aas.twinficient.de/heater/" + company + "/" + celNr + "/" + hzNr + "/" + hgNr
}

extractEndpointPrefix = (epArr) => {
    return if length(arr: epArr) >= 3
    then epArr[0] + "//" + epArr[2]
    else debug.null(type: "string")
}

resolveServerPart = (url) => {
    return if exists url
    then extractEndpointPrefix(epArr: strings.split(v: url, t: "/"))
    else debug.null(type: "string")
}

resolveMapping = (url) => {
    return if exists url
    then record.get(r: endpointMapping, key: url, default: debug.null(type: "string"))  
    else debug.null(type: "string")
}

resolveFirstEndpoint = (sd) => {
    href = if exists sd["endpoints"] and length(arr: sd.endpoints) > 0 and exists sd.endpoints[0]["protocolInformation"] and exists sd.endpoints[0].protocolInformation["href"] then sd.endpoints[0].protocolInformation.href else debug.null(type: "string")
    serverPart = resolveServerPart(url: href)
    mappedPrefix = resolveMapping(url: serverPart)

    return if exists mappedPrefix
    then strings.replaceAll(v: href, t: serverPart, u: mappedPrefix)
    else if exists href then href
    else debug.null(type: "string")
}

lookupFirstEndpointInShellDescriptor = (id) => {
    href = createHeaterShellDescriptorUrl(id: id)
    response = requests.get(url: href,  headers: ["Content-Type": "application/json", "accept": "application/json"])
    return if response.statusCode == 200
    then resolveFirstEndpoint(sd: ejson.parse(data: response.body))
    else debug.null(type: "string")
}


findTechnicalSubmodlUrlInHeaterDefinitionShell = (shell) => {
// we just have one -> so no need for filtering right now
    return if exists shell and exists shell["submodels"] and length(arr: shell.submodels) > 0 and exists shell.submodels[0]["keys"] and length(arr: shell.submodels[0].keys) > 0 and exists shell.submodels[0].keys[0]
    then string(v: shell.submodels[0].keys[0].value)
    else debug.null(type: "string")
}

lookupTechnicalSubmodelUrlFromSmRegistry = (id) => {
    href = smRegistryUrl + "/submodel-descriptors/" + base64UrlEncode(url: id)
    response = requests.get(url: href,  headers: ["Content-Type": "application/json", "accept": "application/json"])
    return if response.statusCode == 200 
    then resolveFirstEndpoint(sd: ejson.parse(data:response.body))//"--" + getPerfomanceFromDynamic(v: dynamic.jsonParse(data: response.body))
    else debug.null(type: "string")
}

lookupTechnicalSubmodelIdFromShell = (shellUrl) => {
    response = requests.get(url: shellUrl,  headers: ["Content-Type": "application/json", "accept": "application/json"])
    return if response.statusCode == 200
    then findTechnicalSubmodlUrlInHeaterDefinitionShell(shell: ejson.parse(data: response.body)) //findDerivedFrom(shell: ejson.parse(data: response.body))
    else debug.null(type: "string")
}

findDevicePerformanceInSubmodel = (sm) => {
    return if exists sm and exists sm["submodelElements"] and length(arr: sm.submodelElements) > 0 
              and exists sm.submodelElements[0]["value"] and length(arr: sm.submodelElements[0].value) > 0
              and exists sm.submodelElements[0].value[0]["value"] and length(arr: sm.submodelElements[0].value[0].value) > 0
              and exists sm.submodelElements[0].value[0].value[0]["value"] and length(arr: sm.submodelElements[0].value[0].value) > 0
              and exists sm.submodelElements[0].value[0].value[0].value[0]["value"]
    then string(v: sm.submodelElements[0].value[0].value[0].value[0].value)
    else debug.null(type: "string")
}

getDevicePerformanceBySubmodelUrl = (smUrl) => {
    response = requests.get(url: smUrl,  headers: ["Content-Type": "application/json", "accept": "application/json"])
    return if response.statusCode == 200
    then findDevicePerformanceInSubmodel(sm: ejson.parse(data: response.body))
    else debug.null(type: "string")
}

getDevicePerfomanceByDerivedFrom = (df) => {
    url = lookupFirstEndpointInShellDescriptor(id: df)
    submodelId = if exists url 
    then lookupTechnicalSubmodelIdFromShell(shellUrl: url)
    else debug.null(type: "string")

    submodelUrl = if exists submodelId
    then lookupTechnicalSubmodelUrlFromSmRegistry(id: submodelId)
    else debug.null(type: "string")

    return if exists submodelUrl
    then getDevicePerformanceBySubmodelUrl(smUrl: submodelUrl)
    else debug.null(type: "string")
}

withDevicePerformance = (t=<-) => {

    performance = t
         |> keep(columns: ["derivedFrom"])
         |> unique(column: "derivedFrom")
         |> map(fn: (r) => ({r with performance: if exists r.derivedFrom then getDevicePerfomanceByDerivedFrom(df: r.derivedFrom) else "-1.0"}))

    result = join.left(
        left: t |> group(columns: []),
        right: performance,
        on: (l, r) => l.derivedFrom == r.derivedFrom,
        as: (l, r) => ({ l with performance: r.performance}),
     ) 

    return result
}

findDerivedFrom = (shell) => {
     return if exists shell and exists shell["derivedFrom"] and exists shell.derivedFrom["keys"] and length(arr: shell.derivedFrom.keys) > 0 and exists  shell.derivedFrom.keys[0]["value"]
     then string(v: shell.derivedFrom.keys[0].value)
     else debug.null(type: "string")
}

lookupShellsDerivedFrom = (url) => {
    response = requests.get(url: url,  headers: ["Content-Type": "application/json", "accept": "application/json"])
    return if response.statusCode == 200
    then findDerivedFrom(shell: ejson.parse(data: response.body))
    else debug.null(type: "string")
}

getDerivedFrom = (company, celNr, hzNr, hgNr) => {
    shellId = createHeaterShellDescriptorId(company: company, celNr: celNr, hzNr: hzNr, hgNr: hgNr)
    endpoint = lookupFirstEndpointInShellDescriptor(id: shellId)
    return if exists endpoint 
    then  lookupShellsDerivedFrom(url: endpoint)
    else debug.null(type: "string")
}

withDerivedFrom = (t=<-) => t |> map(fn: (r) => ({r with derivedFrom: getDerivedFrom(company: r.Company, celNr: r.CelNr, hzNr: r.HzNr, hgNr: r.HgNr)})) 

addPerformance = (t=<-) => {
    toCalculateOn = 
        t |> filter(fn: (r) => exists r.Company and exists r.CelNr and exists r.HzNr and exists r.HgNr)    
          |> last()
          |> withDerivedFrom()
          |> withDevicePerformance()

    result = join.left(
        left: t |> group(columns: []),
        right: toCalculateOn,
        on: (l, r) => l.Company == r.Company and l.CelNr == r.CelNr and l.HzNr == r.HzNr and l.HgNr == r.HgNr,
        as: (l, r) => ({ l with performance: float(v: r.performance)}),
     ) 

    return  result |> group(columns: ["_measurement", "_field", "Company", "CelNr", "HzNr", "HgNr"]) |> sort(columns: ["_time"])
}


from(bucket: "twinficient-aggregates") 
    |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
    |> filter(fn: (r) => r["_measurement"] == "aggregations"  )
    |> filter(fn: (r) => r._field == "RtPerDay" )
    |> filter(fn: (r) => exists r.HzNr and exists r.HgNr and exists r.CelNr and exists r.Company)
    |> filter(fn: (r) => (r["Company"] == "${Company_1}" and r["CelNr"] == "${CelNr_1}" and r["HzNr"] == "${HzNr_1}" and r["HgNr"] == "${HgNr_1}") or (r["Company"] == "${Company_2}" and r["CelNr"] == "${CelNr_2}" and r["HzNr"] == "${HzNr_2}" and r["HgNr"] == "${HgNr_2}"))
    |> addPerformance()   

    |> map(fn: (r) => ({ r with runtime : r._value})) 
    |> map(fn: (r) => ({ r with _value : if exists r.performance then r._value * float(v: r.performance) else 0.0 - 100.0 * r.performance }))   

    |> set(key: "_field", value: "consumption")
    |> group(columns: ["Company", "CelNr", "HzNr", "HgNr"])
    |> aggregateWindow(every: ${AggregationTime}, fn: sum, timeSrc: "_start")
    |> drop(columns: ["_start", "_stop"]) 
    |> yield()
